class Login extends Component {
	constructor() {
		super();
		this.state = {
			show: false,
		};
	}
	handleModal() {
		this.setState({ show: !this.state.show });
	}

	render() {
		return (
			<div>
				<Button
					onClick={() => {
						this.handleModal();
					}}
				>
					{' '}
					Open Modal
				</Button>
				<Modal show={this.state.show}>
					<Modal.Header> Modal Head </Modal.Header>
					<Modal.Body>Hi , React modal is here</Modal.Body>
					<Modal.Footer>
						<Button
							onClick={() => {
								this.handleModal();
							}}
						>
							Close Modal
						</Button>
					</Modal.Footer>
				</Modal>
			</div>
		);
	}
}

export default Login;
